class GithubEventsControllerTest < ActionDispatch::IntegrationTest
  test 'unauthorized if github signature is not valid' do
    Rails.application.credentials.stubs(:github).returns(webhook_secret: 'secret123')

    post(
      '/v1/hooks/github',
      params: CREATE_ISSUE_COMMENT_BODY,
      headers: {
        'CONTENT_TYPE': 'application/json',
        'HTTP_X_HUB_SIGNATURE': 'sha1=invalid'
      }
    )

    assert_response :unauthorized
  end

  test 'success no content if github signature is valid' do
    Rails.application.credentials.stubs(:github).returns(webhook_secret: 'secret123')

    post(
      '/v1/hooks/github',
      params: CREATE_ISSUE_COMMENT_BODY,
      headers: {
        'CONTENT_TYPE': 'application/json',
        'HTTP_X_HUB_SIGNATURE': 'sha1=6bab375acbd6a7ad996c580bb1fb20c84d80771f'
      }
    )

    assert_response :no_content
  end
end
